import React from "react";
import {
  FlatList,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const ChatTextInput = () => {
  const [enteredGoalText, setEnteredGoalText] = React.useState("");
  const [courseGoals, setCourseGoals] = React.useState([]);

  const goalInputHandler = (enteredGoalText) => {
    setEnteredGoalText(enteredGoalText);
  };

  const addGoalHandler = () => {
    setCourseGoals((currentCourseGoals) => [
      ...currentCourseGoals,
      { text: enteredGoalText, id: Math.random().toString() },
    ]);
    setEnteredGoalText("");
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={courseGoals}
        renderItem={(itemData) => {
          return (
            <View>
              <Text style={styles.heading}>{itemData.item.text}</Text>
            </View>
          );
        }}
        keyExtractor={(item, index) => {
          return item.id;
        }}
        alwaysBounceVertical={false}
      />

      <View style={styles.inputContainer}>
        <TouchableOpacity style={styles.regenerateResponseButton}>
          <Icon name="refresh" size={20} color="#000" />
          <Text style={styles.text}>Regenerate Response</Text>
        </TouchableOpacity>

        <View style={styles.inputWrapper}>
          <TextInput
            style={styles.input}
            onChangeText={goalInputHandler}
            placeholder="Type your message.."
            value={enteredGoalText}
          />
          <TouchableOpacity
            style={styles.sendButton}
            onPress={addGoalHandler}
          >
            <Icon name="send" size={20} color="#000" />
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  heading: {
    fontSize: 60,
    fontWeight: "bold",
    justifyContent: "center",
    alignSelf: "center",
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: "#fff",
    padding: 10,
  },
  inputWrapper: {
    flexDirection: "row",
    alignItems: "center",
    flex: 1,
    marginRight: 10,
  },
  input: {
    height: 40,
    flex: 1,
    borderWidth: 1,
    borderRadius: 20,
    paddingHorizontal: 20,
    marginRight: 10,
  },
  sendButton: {
    backgroundColor: "#fff",
    padding: 10,
    borderRadius: 20,
  },
  regenerateResponseButton: {
    backgroundColor: "#007AFF",
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    flexDirection: "row",
    alignItems: "center",
  },
  text: {
    marginLeft: 10,
    color: "#fff",
  },
});

export default ChatTextInput;
