import React from "react";
import {
  SafeAreaView,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { Ionicons } from "@expo/vector-icons";

const ChatTextInput = () => {
  const [text, setText] = React.useState("");

  const onChangeText = (text) => {
    setText(text);
  };

  const sendMessage = () => {
    console.log(text);
  };

  return (
    /*<SafeAreaView>*/
    <View style={{ flex: 1, flexDirection: "row" }}>
      <Text style={styles.heading}>Homie</Text>
      <View style={styles.inputContainer}>
        <TouchableOpacity style={styles.regenerateResponseButton}>
          <Icon name="refresh" size={20} color="#000" />
          <Text style={styles.text}>Regenerate Response</Text>
        </TouchableOpacity>
        <TextInput
          style={styles.input}
          onChangeText={onChangeText}
          onSubmitEditing={sendMessage}
          value={text}
          placeholder="Send a message.."
        />
        <View style={styles.iconContainer}>
          <Icon name="telegram" size={30} color="#000" />
        </View>
      </View>
    </View>
    /*</SafeAreaView>*/
  );
};

const styles = StyleSheet.create({
  heading: {
    fontSize: 60,
    fontWeight: 'bold',
    justifyContent: "center",
    alignSelf: "center",
  },
  input: {
    height: 60,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    paddingRight: 90,
    width: "70%",
    alignSelf: "center",
    //zIndex: 1,
  },
  inputContainer: {
    flex: 1,
    flexDirection: "col",
    justifyContent: "center",
    alignSelf: "flex-end",
  },
  iconContainer: {
    position: "absolute",
    top: 90,
    right: 220,
    //zIndex: 0,
  },
  title: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase",
  },
  text: {
    marginLeft: 10,
  },
  regenerateResponseButton: {
    backgroundColor: "#007AFF",
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    alignSelf: "center",
    marginVertical: 10,
    flexDirection: "row",
    //zIndex: 2,
  },
});

export default ChatTextInput;
