import React from "react";
import {
  SafeAreaView,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { Ionicons } from "@expo/vector-icons";

const ChatTextInput = () => {
  // const [text, setText] = React.useState("");

  // const onChangeText = (message) => {
  //   setText(message);
  // };

  // const sendMessage = () => {
  //   console.log(text);
  //   setText("");
  // };

  const [enteredGoalText, setEnteredGoalText] = React.useState("");
  const [courseGoals, setCourseGoals] = React.useState([]);

  const goalInputHandler = (enteredGoalText) => {
    setEnteredGoalText(enteredGoalText);
  };

  const addGoalHandler = () => {
    setCourseGoals(currentCourseGoals => [...currentCourseGoals, enteredGoalText]);
  };

  return (
    //<SafeAreaView style={{ flex: 1 }}>
    <View style={{ flex: 1 }}>
      {courseGoals.map((goal) => <Text style={styles.heading}> {goal}</Text>)}
      <View style={styles.inputContainer}>
        <TouchableOpacity style={styles.regenerateResponseButton}>
          <Icon name="refresh" size={20} color="#000" />
          <Text style={styles.text}>Regenerate Response</Text>
        </TouchableOpacity>
        <View style={styles.inputWrapper}>
          <TextInput
            style={styles.input}
            //onChangeText={onChangeText}
            onChangeText={goalInputHandler}
            //onSubmitEditing={sendMessage}
            //value={text}
            placeholder="Send a message.."
          />
          <View style={styles.iconContainer}>
            <Icon name="telegram" size={30} color="#000" />
          </View>
          <Button title = "Send message" onPress = {addGoalHandler}></Button>
        </View>
      </View>
    </View>
    //</SafeAreaView>
  );
};

const styles = StyleSheet.create({
  heading: {
    fontSize: 60,
    fontWeight: "bold",
    justifyContent: "center",
    alignSelf: "center",
  },
  input: {
    height: 60,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: "100%",
    alignSelf: "center",
    //zIndex: 1,
  },
  inputContainer: {
    flexDirection: "column",
    justifyContent: "flex-end",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    paddingHorizontal: 16,
    paddingBottom: 16,
  },
  inputWrapper: {
    flexDirection: "row",
    alignItems: "center",
  },
  iconContainer: {
    marginLeft: 16,
  },
  title: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase",
  },
  text: {
    marginLeft: 10,
  },
  regenerateResponseButton: {
    backgroundColor: "#007AFF",
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    alignSelf: "center",
    marginVertical: 10,
    flexDirection: "row",
    //zIndex: 2,
  },
});
export default ChatTextInput;
