import React from "react";
import {
  FlatList,
  ScrollView,
  Button,
  SafeAreaView,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { Ionicons } from "@expo/vector-icons";

import ChatBox from "./ChatBox"

const ChatTextInput = () => {
  const [enteredGoalText, setEnteredGoalText] = React.useState("");
  const [courseGoals, setCourseGoals] = React.useState([{text : "Hii", id : 1}]);


  const goalInputHandler = (enteredGoalText) => {
    setEnteredGoalText(enteredGoalText);
  };

  const addGoalHandler = () => {
    setCourseGoals((currentCourseGoals) => [
      ...currentCourseGoals,
      { text: enteredGoalText, id: Math.random().toString() },
    ]);
    setEnteredGoalText("");
  };

  return (
    //<SafeAreaView style={{ flex: 1 }}>
    <View style={{ flex: 1 }}>
      {/*<ScrollView>{courseGoals.map((goal) => <Text style={styles.heading} key={goal}> {goal}</Text>)}</ScrollView>*/}
      <View style = {styles.chatDisplay}>
        <FlatList 
          data={courseGoals}
          renderItem={(itemData) => {
            return (

              <ChatBox data =  {itemData}></ChatBox>
            );
          }}
          keyExtractor={(item, index) => {
            return item.id;
          }}
          alwaysBounceVertical={false}
        />
      </View>

      <View style={styles.inputContainer}>
        <TouchableOpacity style={styles.regenerateResponseButton}>
          <Icon name="refresh" size={20} color="#000" />
          <Text style={styles.text}>Regenerate Response</Text>
        </TouchableOpacity>
        <View style={styles.inputWrapper}>
          <TextInput
            style={styles.input}
            onChangeText={goalInputHandler}
            placeholder="Type your message.."
            value={enteredGoalText}
          />
          {/* <View style={styles.iconContainer}>
            <Icon name="telegram" size={30} color="#000" />
          </View> */}
          <Button title="Send message" onPress={addGoalHandler}></Button>
        </View>
      </View>
    </View>
    //</SafeAreaView>
  );
};

const styles = StyleSheet.create({
  heading: {
    fontSize: 60,
    fontWeight: "bold",
    justifyContent: "center",
    alignSelf: "center",
  },
  chatDisplay : {
    height : "60vh",
    display : "flex",
    justifyContent : "center",
    zIndex : "0",
  },
  input: {
    height: "10vh",
    margin: 12,
    borderWidth: 1,
    padding: 10,
    width: "100%",
    alignSelf: "center",
    //zIndex: 1,
  },
  inputContainer: {
    flexDirection: "column",
    justifyContent: "flex-end",
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    paddingHorizontal: 16,
    paddingBottom: 16,
    zIndex : "1000",
    backgroundColor : "red"
  },
  inputWrapper: {
    flexDirection: "row",
    alignItems: "center",
  },
  iconContainer: {
    marginLeft: 16,
  },
  title: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase",
  },
  text: {
    marginLeft: 10,
  },
  regenerateResponseButton: {
    backgroundColor: "#007AFF",
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    alignSelf: "center",
    marginVertical: 10,
    flexDirection: "row",
    //zIndex: 2,
  },
});
export default ChatTextInput;
