import React from "react";
import {
  SafeAreaView,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
  View,
} from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";
import { Ionicons } from "@expo/vector-icons";

const ChatTextInput = () => {
  const [text, onChangeText] = React.useState("");

  return (
    /*<SafeAreaView>*/
    <View style={styles.inputContainer}>
      <TouchableOpacity style={styles.regenerateResponseButton}>
        <Icon name="refresh" size={20} color="#000" />
        <Text style={styles.title}>Regenerate Response</Text>
      </TouchableOpacity>
      <TextInput
        style={styles.input}
        onChangeText={onChangeText}
        value={text}
        placeholder="Send a message.."
      />
      <View style={styles.iconContainer}>
        <Icon name="telegram" size={30} color="#000" />
      </View>
    </View>
    /*</SafeAreaView>*/
  );
};

const styles = StyleSheet.create({
  input: {
    height: 60,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    paddingRight: 90,
    width: "70%",
    alignSelf: "center",
    //zIndex: 1,
  },
  inputContainer: {
    flex: 1,
    flexDirection: "col",
    justifyContent: "center",
    alignSelf: "flex-end",
  },
  iconContainer: {
    position: "absolute",
    top: 90,
    right: 220,
    //zIndex: 0,
  },
  title: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    alignSelf: "center",
    textTransform: "uppercase",
  },
  regenerateResponseButton: {
    backgroundColor: "#007AFF",
    borderRadius: 10,
    paddingVertical: 10,
    paddingHorizontal: 12,
    alignSelf: "center",
    marginVertical: 10,
    //zIndex: 2,
  },
});

export default ChatTextInput;
